                            success: function(response){
                            $("#mail-status").show();
                            $('#loader-icon').hide();
                            if(response.type == "error") {
                            $('#send-message').show();
                            $("#mail-status").attr("class","error");
                            } else if(response.type == "message"){
                            $('#send-message').hide();
                            $("#mail-status").attr("class","success");
                            }
                            $("#mail-status").html(response.text);
                            },
                            error: function(){}
                            });
                            }));
                            });
